ee:message.ee:set-payload:
%dw 2.0
output application/java
var data = payload splitBy ":"
---
{
    gameId: (data[0] splitBy " ")[-1],
    rounds: (data[1] splitBy ";") 
                map ((cubes, index) -> (cubes splitBy ","
                    map (line, index) -> trim(line) splitBy " ")
                        map {
                            ($[1]): $[0]
                        }) map {($)}
}

